const MAC_ENCODINGS = {
  'x-mac-croatian': ' ',
  'x-mac-gaelic': ' ',
  'x-mac-greek': ' \u00AD',
  'x-mac-icelandic': ' ',
  'x-mac-inuit': ' ',
  'x-mac-ce': ' ',
  'x-mac-romanian': ' ',
