import React, { useEffect, useRef, useState } from "react";
import { IoSearchOutline, IoCloseOutline } from "react-icons/io5";
import { AnimatePresence, motion } from "framer-motion";
import Hider from "../../../../utilsComponents/Hider/Hider";
import "./SearchBar.sass";

const SearchBar = () => {
  const [showSearch, setShowSearch] = useState(false);
  const input = useRef(null);

  const handleShowSearch = () => {
    setShowSearch(!showSearch);
  };

  useEffect(() => {
    if (input.current && showSearch) input.current.focus();
  }, [showSearch]);

  return (
    <>
      <div className="search_bar_container" onClick={handleShowSearch}>
        <div className="placeholder">Search here...</div>
        <div className="icon">
          <IoSearchOutline />
        </div>
      </div>
      <AnimatePresence>
        {showSearch && (
          <Hider onClick={handleShowSearch}>
            <motion.div
              className="search_input_container"
              onClick={(e) => {
                e.stopPropagation();
              }}
            >
              <div className="input">
                <div className="left icon">
                  <IoSearchOutline />
                </div>
                <input type="text" ref={input} placeholder="What are you looking for ?" />
                <div className="right icon">
                  <IoCloseOutline />
                </div>
              </div>
              <div className="result_list"></div>
            </motion.div>
          </Hider>
        )}
      </AnimatePresence>
    </>
  );
};

export default SearchBar;
