import React, { useEffect, useState } from "react";
import { IoIosArrowBack, IoIosArrowForward } from "react-icons/io";
import "./PaginationSlider.sass";

const PaginationSlider = ({ loading, callBackPagination = (index) => {}, pageCount = 10 }) => {
  const [spacedLeft, setSpacedLeft] = useState(false);
  const [spacedRight, setSpacedRight] = useState(false);

  const [leftRow, setLeftRow] = useState([]);
  const [rightRow, setRightRow] = useState([]);
  const [middleRow, setMiddleRow] = useState([]);

  const [index, setIndex] = useState(0);
  const countToShow = 9;

  const handleSpaced = () => {
    if (pageCount < countToShow) clearSpace();
    else {
      if (index - 6 > 0) fillLeftFirst();
      if (index + 6 < pageCount) fillRightEnd();
      if (index + 6 > pageCount && index - 6 > 0) fillMiddle();
      else {
      }
    }
  };
  const clearSpace = () => {
    setSpacedLeft(false);
    setSpacedRight(false);
    setLeftRow([...Array(pageCount).keys()]);
    setMiddleRow([]);
    setRightRow([]);
  };

  const fillLeftFirst = (limit = 3) => {
    console.log("left");
    setSpacedLeft(true);
    setLeftRow([...Array(limit).keys()]);
  };

  const fillMiddle = () => {
    console.log("middle");
    let data = [];
    for (let i = index - 2; i < index + 3; i++) {
      data.push(i);
    }
    setMiddleRow(data);
  };

  const fillRightEnd = () => {
    console.log("right");
    let data = [];
    for (let i = pageCount - 3; i < pageCount; i++) {
      data.push(i);
    }
    setSpacedRight(true);
    setRightRow(data);
  };

  const next = () => {
    console.log(index + 1 < pageCount - 2, index + 1);
    if (index + 1 < pageCount) setIndex(index + 1);
  };
  const before = () => {
    if (index - 1 >= 0) setIndex(index - 1);
  };

  useEffect(() => {
    handleSpaced();
  }, [index]);

  const Blocks = ({ indexes }) => {
    return (
      <>
        {indexes.map((row) => (
          <div
            key={row}
            className={`block_index ${index === row ? "active" : ""}`}
            onClick={() => {
              setIndex(row);
              callBackPagination(row);
            }}
          >
            {row + 1}
          </div>
        ))}
      </>
    );
  };

  return (
    <div className="pagination_container">
      <div className="left" onClick={before}>
        <IoIosArrowBack className="back" />
      </div>
      <div className="pages_index">
        <Blocks indexes={leftRow} />
        {spacedLeft && <div className="spaces">...</div>}
        <Blocks indexes={middleRow} />
        {spacedRight && <div className="spaces">...</div>}
        <Blocks indexes={rightRow} />
      </div>
      <div className="right" onClick={next}>
        <IoIosArrowForward className="forward" />
      </div>
    </div>
  );
};

export default PaginationSlider;
