import React, { useEffect, useState } from "react";
import "../Input.sass";

const Checkbox = ({
  type = "",
  defaultValue = "",
  title = "",
  name = "",
  onChange = (e) => {},
  disabled = false,
  required = false,
  fullWidth = false,
}) => {
  const [checked, setChecked] = useState(!defaultValue ? "false" : defaultValue);

  const handleValue = () => {
    if (checked === "true") setChecked("false");
    else setChecked("true");
    onChange({ target: { value: checked === "true" ? "false" : "true", name: name } });
  };

  useEffect(() => {
    setChecked(defaultValue);
    onChange({ target: { value: defaultValue, name: name } });
  }, [defaultValue]);

  return (
    <div className={`input_ checkbox ${fullWidth ? "fullwidth" : ""}`}>
      <div className={`checkbox_container {checked === "true" ? "on" : "off"}`} onClick={handleValue}>
        <div className={`state ${checked === "true" ? "on" : "off"}`}>{}</div>
      </div>
      <label htmlFor={name}>{title}</label>
    </div>
  );
};

export default Checkbox;
