import CheckIcon from "../../assets/icons/CheckIcon";
import CrossIcon from "../../assets/icons/CrossIcon";

export const BooleanMark = (value, className = "") => {
  return (
    <>
      {value + "" === "true" ? (
        <div className={"icon_row_table checked " + className}>
          <CheckIcon />
        </div>
      ) : (
        <div className={"icon_row_table unchecked " + className}>
          <CrossIcon />
        </div>
      )}
    </>
  );
};

const test = [
  { 0: "Xorem ing elit.", 1: "5", 2: "true", 3: "Lorem ing elit.", test: { po: "mety" } },
  { 0: "Borem ing elit.", 1: 2, 2: "false", 3: "Lorem ing elit.", test: { po: "mety ds" } },
  { 0: "Sorem ing elit.", 1: 1, 2: "false", 3: "Lorem ing elit.", test: { po: "mety" } },
  { 0: "Dorem ing elit.", 1: "3", 2: "true", 3: "Lorem ing elit.", test: { po: "mety" } },
  { 0: "Forem ing elit.", 1: 10, 2: "true", 3: "Lorem ing elit.", test: { po: "mety" } },
];

export const sortBy = (datas = [], sortedBy, direction = "asc") => {
