import React, { useState } from "react";
import { Document, Page, Text, View, StyleSheet, PDFViewer, PDFDownloadLink } from "@react-pdf/renderer";
import ReactPDF from "@react-pdf/renderer";
import Home from "../Home/Home";
import Table from "../../utilsComponents/Table/Table";
import Input from "../../utilsComponents/Input/Input";
import Loader from "../../utilsComponents/Hider/Loader/Loader";
import Modal from "../../utilsComponents/Modal/Modal";

// Create styles
const styles = StyleSheet.create({
  page: {
    flexDirection: "column",
    backgroundColor: "#fff",
  },
  section: {
    margin: 10,
    padding: 10,
    fontSize: 15,
    flexDirection: "row",
  },
});

const PdfTest = () => {
  const [showPDF, setShowPDF] = useState(true);
  const handlePdfView = () => {
    setShowPDF(!showPDF);
  };
  return (
    <div className="inner">
      <PDFDownloadLink document={<MyDocument />} fileName="test.pdf">
        {({ blob, url, loading, error }) => (loading ? <Loader /> : <button>Download now</button>)}
      </PDFDownloadLink>
      <button onClick={handlePdfView}> Open PDF Viewer </button>

      <Modal>
        <div className="container_pdf" style={{ height: "80vh", width: "40rem" }}>
          <PDFViewer style={{ width: "100%", height: "100%", backgroundColor: "transparent" }} showToolbar={false}>
            <MyDocument />
          </PDFViewer>
        </div>
      </Modal>
    </div>
  );
};

// Create Document Component
const MyDocument = () => (
  <Document>
    {[1, 1, 1].map((d, key) => (
      <Page size="A4" style={styles.page} key={d.key}>
        <View style={styles.section}>
          <Text style={{ marginRight: 20 }}>Hello world!</Text>
          <View>
            <Text>Hello</Text>
          </View>
        </View>
      </Page>
    ))}
    {/* <Page size="A4" style={styles.page}>
      <View style={styles.section}>
        <Text>Section #1</Text>
      </View>
      <View style={styles.section}>
        <Text>Section #2</Text>
      </View>
    </Page> */}
  </Document>
);
export default PdfTest;
