import CheckIcon from "../../assets/icons/CheckIcon";
import CrossIcon from "../../assets/icons/CrossIcon";

export const BooleanMark = (value, className = "") => {
  return (
    <>
      {value + "" === "true" ? (
        <div className={"icon_row_table checked " + className}>
          <CheckIcon />
        </div>
      ) : (
        <div className={"icon_row_table unchecked " + className}>
          <CrossIcon />
        </div>
      )}
    </>
  );
};

const test = [
  { 0: "Xorem ing elit.", 1: "5", 2: "true", 3: "Lorem ing elit.", test: { po: "a mety" } },
  { 0: "Borem ing elit.", 1: 2, 2: "false", 3: "Lorem ing elit.", test: { po: "bmety ds" } },
  { 0: "Sorem ing elit.", 1: 1, 2: "false", 3: "Lorem ing elit.", test: { po: "xmety" } },
  { 0: "Dorem ing elit.", 1: "3", 2: "true", 3: "Lorem ing elit.", test: { po: "d mety" } },
  { 0: "Forem ing elit.", 1: 10, 2: "true", 3: "Lorem ing elit.", test: { po: "p mety" } },
];

const getValue = (obj, keys) => {
  let value = obj;
  if (Array.isArray(keys)) {
    for (let i = 0; i < keys.length; i++) {
      value = value[keys[i]];
    }
  } else value = value[keys];

  return value;
};

export const sortBy = (datas = [], sortedBy, direction = "asc") => {
  return datas.sort((a, b) => {
    let a_value = getValue(a, sortedBy);
    let b_value = getValue(b, sortedBy);

    if (a_value > b_value) {
      return direction === "asc" ? 1 : -1;
    }
    if (a_value < b[sortedBy]) {
      return direction === "asc" ? -1 : 1;
    }
    return 0;
  });
};

console.log(sortBy(test, ["test", "po"], "desc"));
