import LogoDefault from "../../../assets/svg/LogoDefault";
import RowInput from "../RowInput/RowInput";
import UseHandleForm from "./UseHandleForm";
import { FormSignUpData } from "./formData";
import "./FormSignUp.sass";

const FormSignUp = ({ handleSign = () => {} }) => {
  const handleSubmit = () => {
    console.log("FormSignUp");
  };

  const { backStep, formData, nextStep, moveStep, handleForm, handleInputForm, step } = UseHandleForm(3, [null, null, handleSubmit]);

  return (
    <div className="sign_up_form">
      <div className="logo">
        <LogoDefault />
      </div>
      <div className="title">Sign up</div>
      <div className="subtitle">Come with us in this incredible journey.</div>
      <div className="slider">
        {[...Array(FormSignUpData.length).keys()].map((d, k) => (
          <div
            className={`slide ${step === d + 1 ? "slide_on" : ""}`}
            key={d}
            onClick={() => {
              moveStep(d + 1);
            }}
          ></div>
        ))}
      </div>
      <form action="" method="post" onSubmit={handleForm}>
        {FormSignUpData.map((row, k) => {
          return k === step
            ? row.inputs.map((input, k_input) => (
                <RowInput
                  title={input.title}
                  key={k_input}
                  type={input.type}
                  value={formData[input.name]}
                  id={input.name}
                  name={input.name}
                  fullWidth
                  onChange={handleInputForm}
                />
              ))
            : "";
        })}
        {step === 1 ? (
          <>
            <RowInput
              title="Your email"
              type="email"
              // required
              value={formData.email}
              id="email"
              name="email"
              fullWidth
              onChange={handleInputForm}
            />
            <RowInput
              title="Your password"
              type="password"
              required
              value={formData.password}
              id="password"
              name="password"
              fullWidth
              onChange={handleInputForm}
            />
            <div className="button">
              <button>Next</button>
            </div>
          </>
        ) : (
          ""
        )}
        {step === 2 ? (
          <>
            <RowInput
              title="Hzllo world"
              type="text"
              value={formData.hello}
              id="hello"
              name="hello"
              fullWidth
              onChange={handleInputForm}
            />
            <div className="button">
              <button>Next</button>
            </div>
          </>
        ) : (
          ""
        )}

        <div className="sign_up_link">
          <div className="text">Already have an account ?</div>
          <div className="link" onClick={handleSign}>
            Sign in.
          </div>
        </div>
      </form>
    </div>
  );
};

export default FormSignUp;
