import React, { useEffect, useState } from "react";
import "../Input.sass";

const Checkbox = ({
  type = "",
  defaultValue = "",
  title = "",
  name = "",
  onChange = (e) => {},
  disabled = false,
  required = false,
  fullWidth = false,
  constraint = (val) => {
    return true;
  },
}) => {
  const [checked, setChecked] = useState(!defaultValue ? "false" : defaultValue);
  const [value, setValue] = useState(defaultValue);

  const handleValue = (e) => {
    setValue(e.target.value);
    onChange(e);
  };

  useEffect(() => {
    setValue(defaultValue);
    onChange({ target: { value: defaultValue, name: name } });
  }, [defaultValue]);

  return (
    <div className={`input_ ${fullWidth ? "fullwidth" : ""}`}>
      <div className="checkbox_container">
        <div className={`state ${checked === "true" ? "on" : "off"}`}>{}</div>
      </div>
      <label htmlFor={name}>{title}</label>
    </div>
  );
};

export default Checkbox;
