import React, { useState } from "react";
import Bg from "../../../assets/img/brume.jpg";
import "./ActuSwiper.sass";
import ArrowRight from "../../../assets/svg/ArrowRight";

const ActuSwiper = () => {
  const [content, setContent] = useState([1, 2, 1]);
  const [percent, setPercent] = useState(0);
  const next = () => {
    setPercent(percent + 100);
  };
  const back = () => {
    setPercent(percent - 100);
  };
  return (
    <div className="actu_swiper_container">
      <div className="counter_part">
        <div className="round"></div>
        <div className="round"></div>
        <div className="round"></div>
      </div>
      <div className="switcher_part">
        <div className="go_left">
          <ArrowRight />
        </div>
        <div className="go_right">
          <ArrowRight />
        </div>
      </div>
      {content.map((c, key) => (
        <div className="block_actu" key={key} style={{ transform: `translateX()` }}>
          <div className="actu_pic">
            <img src={Bg} alt="" />
          </div>
        </div>
      ))}

      <div className="overlay"></div>
    </div>
  );
};

export default ActuSwiper;
